// Helper functions
export const getAIContentById = (id: string) => {
  return Object.values(aiGeneratedContent).find(content => content.id === id);
};

export const getAIContentByType = (type: string) => {
  return Object.values(aiGeneratedContent).filter(content => content.content_type === type);
};

export const getAIAssistedContent = (assistanceLevel?: number) => {
  const content = Object.values(aiGeneratedContent);
  if (assistanceLevel) {
    return content.filter(c => c.ai_assistance_level >= assistanceLevel);
  }
  return content.filter(c => c.ai_generated || c.ai_enhanced || c.ai_curated || c.ai_moderated);
};

